import React, { useState, useEffect } from 'react';
import Map from './components/Map';
import OrderPanel from './components/OrderPanel';
import DroneStatus from './components/DroneStatus';
import './App.css';

function App() {
  const [orders, setOrders] = useState([]);
  const [drones, setDrones] = useState([]);
  const [routes, setRoutes] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [locations, setLocations] = useState({
    gates: [],
    canteens: [],
    dorms: []
  });
  const [isSimulationRunning, setIsSimulationRunning] = useState(false);

  // 加载GeoJSON数据
  useEffect(() => {
    const loadGeoJSONData = async () => {
      try {
        // 加载校门数据
        const gatesResponse = await fetch('/data/gates.geojson');
        const gatesData = await gatesResponse.json();

        // 加载食堂数据
        const canteensResponse = await fetch('/data/canteens.geojson');
        const canteensData = await canteensResponse.json();

        // 加载宿舍数据
        const dormsResponse = await fetch('/data/dorms.geojson');
        const dormsData = await dormsResponse.json();

        // 提取地点信息
        const gates = gatesData.features.map(feature => ({
          id: feature.properties.id || `gate_${Math.random()}`,
          name: feature.properties.name || `校门${Math.random().toString(36).substr(2, 5)}`,
          coords: feature.geometry.coordinates,
          type: 'gate'
        }));

        const canteens = canteensData.features.map(feature => ({
          id: feature.properties.id || `canteen_${Math.random()}`,
          name: feature.properties.name || feature.properties['name:zh'] || `食堂${Math.random().toString(36).substr(2, 5)}`,
          coords: feature.geometry.coordinates[0][0], // 取多边形的第一个点
          type: 'canteen'
        }));

        const dorms = dormsData.features.map(feature => ({
          id: feature.properties.id || `dorm_${Math.random()}`,
          name: feature.properties.name || feature.properties['name:zh'] || `宿舍${Math.random().toString(36).substr(2, 5)}`,
          coords: feature.geometry.coordinates[0][0], // 取多边形的第一个点
          type: 'dorm'
        }));

        setLocations({ gates, canteens, dorms });
        console.log('地点数据加载完成:', { gates, canteens, dorms });
      } catch (error) {
        console.error('加载地点数据失败:', error);
        // 使用默认数据
        setLocations({
          gates: [
            { id: 'gate_1', name: '东门', coords: [116.325263, 40.0053343], type: 'gate' },
            { id: 'gate_2', name: '西门', coords: [116.315263, 40.0053343], type: 'gate' },
            { id: 'gate_3', name: '南门', coords: [116.320263, 40.0003343], type: 'gate' }
          ],
          canteens: [
            { id: 'canteen_1', name: '观畴园餐厅（万人大食堂）', coords: [116.315263, 40.0053343], type: 'canteen' },
            { id: 'canteen_2', name: '紫荆园餐厅', coords: [116.322763, 40.0103343], type: 'canteen' },
            { id: 'canteen_3', name: '清芬园餐厅', coords: [116.318763, 40.0073343], type: 'canteen' }
          ],
          dorms: [
            { id: 'dorm_1', name: '紫荆学生公寓1号楼', coords: [116.320263, 40.0103343], type: 'dorm' },
            { id: 'dorm_2', name: '紫荆学生公寓2号楼', coords: [116.321263, 40.0103343], type: 'dorm' },
            { id: 'dorm_3', name: '紫荆学生公寓3号楼', coords: [116.322263, 40.0103343], type: 'dorm' }
          ]
        });
      }
    };

    loadGeoJSONData();
  }, []);

  // 加载航线数据
  useEffect(() => {
    fetch('/route_planning_results.json')
      .then(response => response.json())
      .then(data => {
        setRoutes(data);
        console.log('航线数据加载完成:', data);
      })
      .catch(error => {
        console.error('加载航线数据失败:', error);
        // 使用示例数据
        setRoutes({
          routes: {
            canteen_to_dorm: [],
            gate_to_dorm: []
          }
        });
      });
  }, []);

  // 添加新订单
  const addOrder = (orderData) => {
    const newOrder = {
      id: `ORDER_${Date.now()}`,
      orderNumber: `THU_${Math.random().toString(36).substr(2, 9).toUpperCase()}`,
      createTime: new Date().toISOString(),
      status: 'pending', // pending, assigned, delivering, completed
      ...orderData
    };

    setOrders(prev => [...prev, newOrder]);

    // 如果模拟正在运行，自动分配无人机
    if (isSimulationRunning) {
      assignDroneToOrder(newOrder);
    }
  };

  // 分配无人机到订单
  const assignDroneToOrder = (order) => {
    const availableDrone = {
      id: `DRONE_${Date.now()}`,
      orderId: order.id,
      status: 'assigned',
      currentPosition: order.startPoint,
      targetPosition: order.endPoint,
      path: [], // 将从航线数据中获取
      speed: 10, // 米/秒
      altitude: 100 // 米
    };

    // 从航线数据中查找最佳路径
    if (routes) {
      const bestRoute = findBestRoute(order.startPoint, order.endPoint);
      if (bestRoute) {
        availableDrone.path = bestRoute.path;
        availableDrone.altitude = bestRoute.height;
      }
    }

    setDrones(prev => [...prev, availableDrone]);

    // 更新订单状态
    setOrders(prev => 
      prev.map(o => 
        o.id === order.id ? { ...o, status: 'assigned' } : o
      )
    );
  };

  // 查找最佳航线
  const findBestRoute = (start, end) => {
    if (!routes) return null;

    // 简化版本：随机选择一条可用航线
    const allRoutes = [...routes.routes.canteen_to_dorm, ...routes.routes.gate_to_dorm];
    if (allRoutes.length > 0) {
      return allRoutes[Math.floor(Math.random() * allRoutes.length)];
    }
    return null;
  };

  // 开始模拟
  const startSimulation = () => {
    setIsSimulationRunning(true);

    // 为所有待分配的订单分配无人机
    orders.forEach(order => {
      if (order.status === 'pending') {
        assignDroneToOrder(order);
      }
    });
  };

  // 停止模拟
  const stopSimulation = () => {
    setIsSimulationRunning(false);
  };

  // 重置系统
  const resetSystem = () => {
    setOrders([]);
    setDrones([]);
    setSelectedOrder(null);
    setIsSimulationRunning(false);
  };

  // 更新无人机位置（模拟飞行）
  useEffect(() => {
    const interval = setInterval(() => {
      setDrones(prev => 
        prev.map(drone => {
          if (drone.status === 'delivering' && drone.path.length > 0) {
            // 更新无人机位置
            const currentIndex = Math.floor(Math.random() * drone.path.length);
            return {
              ...drone,
              currentPosition: drone.path[currentIndex],
              status: currentIndex >= drone.path.length - 1 ? 'completed' : 'delivering'
            };
          }
          return drone;
        })
      );
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <div className="header">
        <h1>清华大学无人机外卖实时调度系统</h1>
        <button onClick={resetSystem} className="reset-btn">
          重置系统
        </button>
      </div>

      <div className="main-content">
        <div className="left-panel">
          <OrderPanel 
            onAddOrder={addOrder}
            orders={orders}
            onSelectOrder={setSelectedOrder}
            selectedOrder={selectedOrder}
          />
          <DroneStatus drones={drones} />
        </div>

        <div className="map-container">
          <Map 
            orders={orders}
            drones={drones}
            routes={routes}
            selectedOrder={selectedOrder}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
