import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';
import { zhCN } from 'date-fns/locale';
import './OrderPanel.css';

const OrderPanel = ({ onAddOrder, orders, onSelectOrder, selectedOrder, locations, isSimulationRunning }) => {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    startPoint: '',
    endPoint: '',
    description: ''
  });

  // ÁõëÂê¨locationsÊï∞ÊçÆÂèòÂåñ
  useEffect(() => {
    console.log('OrderPanel: locationsÊï∞ÊçÆÊõ¥Êñ∞:', locations);
    console.log('Ê†°Èó®Êï∞Èáè:', locations.gates?.length || 0);
    console.log('È£üÂ†ÇÊï∞Èáè:', locations.canteens?.length || 0);
    console.log('ÂÆøËàçÊï∞Èáè:', locations.dorms?.length || 0);
  }, [locations]);

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log('Êèê‰∫§Ë°®ÂçïÊï∞ÊçÆ:', formData);
    console.log('ÂèØÁî®Âú∞ÁÇπÊï∞ÊçÆ:', locations);
    console.log('Ê†°Èó®Êï∞Èáè:', locations.gates?.length || 0);
    console.log('È£üÂ†ÇÊï∞Èáè:', locations.canteens?.length || 0);
    console.log('ÂÆøËàçÊï∞Èáè:', locations.dorms?.length || 0);

    // Ê£ÄÊü•ÊòØÂê¶ÈÄâÊã©‰∫ÜËµ∑ÁÇπÂíåÁªàÁÇπ
    if (!formData.startPoint || !formData.endPoint) {
      alert('ËØ∑ÈÄâÊã©Ëµ∑ÁÇπÂíåÁªàÁÇπ');
      return;
    }

    const startLocation = [...locations.gates, ...locations.canteens].find(loc => loc.id === formData.startPoint);
    const endLocation = locations.dorms.find(loc => loc.id === formData.endPoint);

    console.log('ÊâæÂà∞ÁöÑËµ∑ÁÇπ:', startLocation);
    console.log('ÊâæÂà∞ÁöÑÁªàÁÇπ:', endLocation);

    if (!startLocation || !endLocation) {
      alert('ËØ∑ÈÄâÊã©ÊúâÊïàÁöÑËµ∑ÁÇπÂíåÁªàÁÇπ');
      return;
    }

    const orderData = {
      startPoint: startLocation.coords,
      endPoint: endLocation.coords,
      startLocation: startLocation.name,
      endLocation: endLocation.name,
      description: formData.description
    };

    onAddOrder(orderData);
    setFormData({ startPoint: '', endPoint: '', description: '' });
    setShowForm(false);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const getStatusColor = (status) => {
    const colors = {
      'pending': '#f39c12',
      'assigned': '#3498db',
      'delivering': '#9b59b6',
      'completed': '#27ae60'
    };
    return colors[status] || '#95a5a6';
  };

  const getStatusText = (status) => {
    const statusMap = {
      'pending': 'ÂæÖÂàÜÈÖç',
      'assigned': 'Â∑≤ÂàÜÈÖç',
      'delivering': 'ÈÖçÈÄÅ‰∏≠',
      'completed': 'Â∑≤ÂÆåÊàê'
    };
    return statusMap[status] || status;
  };

  return (
    <div className="order-panel">
      <div className="panel-header">
        <h3>ËÆ¢ÂçïÁÆ°ÁêÜ</h3>
        <button 
          className="add-order-btn"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? 'ÂèñÊ∂à' : 'Ê∑ªÂä†ËÆ¢Âçï'}
        </button>
      </div>

      {!isSimulationRunning && (
        <div className="simulation-notice">
          <p>‚ö†Ô∏è ËØ∑ÂÖàÁÇπÂáª"ÂºÄÂßãÊ®°Êãü"ÊåâÈíÆÂêØÂä®ÈÖçÈÄÅÁ≥ªÁªü</p>
        </div>
      )}



      {showForm && (
        <form className="order-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Ëµ∑ÁÇπ‰ΩçÁΩÆ:</label>
            <select
              name="startPoint"
              value={formData.startPoint}
              onChange={handleInputChange}
              required
            >
              <option value="">ËØ∑ÈÄâÊã©Ëµ∑ÁÇπ</option>
              <optgroup label="Ê†°Èó®">
                {locations.gates.map(option => (
                  <option key={option.id} value={option.id}>
                    {option.name}
                  </option>
                ))}
              </optgroup>
              <optgroup label="È£üÂ†Ç">
                {locations.canteens.map(option => (
                  <option key={option.id} value={option.id}>
                    {option.name}
                  </option>
                ))}
              </optgroup>
            </select>
          </div>

          <div className="form-group">
            <label>ÁªàÁÇπ‰ΩçÁΩÆ:</label>
            <select
              name="endPoint"
              value={formData.endPoint}
              onChange={handleInputChange}
              required
            >
              <option value="">ËØ∑ÈÄâÊã©ÁªàÁÇπ</option>
              {locations.dorms.map(option => (
                <option key={option.id} value={option.id}>
                  {option.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>ËÆ¢ÂçïÊèèËø∞:</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              placeholder="ÂèØÈÄâÔºöÊèèËø∞ËÆ¢ÂçïÂÜÖÂÆπ"
              rows="2"
            />
          </div>

          <button type="submit" className="submit-btn">
            ÂàõÂª∫ËÆ¢Âçï
          </button>
        </form>
      )}

      <div className="orders-list">
        <h4>ËÆ¢ÂçïÂàóË°® ({orders.length})</h4>
        {orders.length === 0 ? (
          <p className="no-orders">ÊöÇÊó†ËÆ¢Âçï</p>
        ) : (
          orders.map(order => (
            <div 
              key={order.id}
              className={`order-item ${selectedOrder?.id === order.id ? 'selected' : ''}`}
              onClick={() => onSelectOrder(order)}
            >
              <div className="order-header">
                <span className="order-number">{order.orderNumber}</span>
                <span 
                  className="order-status"
                  style={{ backgroundColor: getStatusColor(order.status) }}
                >
                  {getStatusText(order.status)}
                </span>
              </div>

              <div className="order-details">
                <div className="location-info">
                  <div className="start-location">
                    <span className="location-icon">üìç</span>
                    <span className="location-text">{order.startLocation}</span>
                  </div>
                  <div className="end-location">
                    <span className="location-icon">üéØ</span>
                    <span className="location-text">{order.endLocation}</span>
                  </div>
                </div>

                {order.description && (
                  <div className="order-description">
                    {order.description}
                  </div>
                )}

                <div className="order-time">
                  {format(new Date(order.createTime), 'yyyy-MM-dd HH:mm:ss', { locale: zhCN })}
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default OrderPanel;
