    e.preventDefault();

    const startLocation = locationOptions.find(loc => loc.value === formData.startPoint);
    const endLocation = locationOptions.find(loc => loc.value === formData.endPoint);

    if (!startLocation || !endLocation) {
      alert('请选择有效的起点和终点');
      return;
    }

    const orderData = {
      startPoint: startLocation.coords,
      endPoint: endLocation.coords,
      startLocation: startLocation.label,
      endLocation: endLocation.label,
      description: formData.description
    };

    onAddOrder(orderData);
    setFormData({ startPoint: '', endPoint: '', description: '' });
    setShowForm(false);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const getStatusColor = (status) => {
    const colors = {
      'pending': '#f39c12',
      'assigned': '#3498db',
      'delivering': '#9b59b6',
      'completed': '#27ae60'
    };
    return colors[status] || '#95a5a6';
  };

  const getStatusText = (status) => {
    const statusMap = {
      'pending': '待分配',
      'assigned': '已分配',
      'delivering': '配送中',
      'completed': '已完成'
    };
    return statusMap[status] || status;
  };

  return (
