            </select>
          </div>

          <div className="form-group">
            <label>终点位置:</label>
            <select
              name="endPoint"
              value={formData.endPoint}
              onChange={handleInputChange}
              required
            >
              <option value="">请选择终点</option>
              {locationOptions.map(option => (
