import React, { useState, useEffect } from 'react';
import Map from './components/Map';
import OrderPanel from './components/OrderPanel';
import DroneStatus from './components/DroneStatus';
import './App.css';

function App() {
  const [orders, setOrders] = useState([]);
  const [drones, setDrones] = useState([]);
  const [routes, setRoutes] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [locations, setLocations] = useState({
    gates: [],
    canteens: [],
    dorms: []
  });
  const [isSimulationRunning, setIsSimulationRunning] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  // Âä†ËΩΩGeoJSONÊï∞ÊçÆ
  useEffect(() => {
    const loadGeoJSONData = async () => {
      setIsLoading(true);
      try {
        // Âä†ËΩΩÊ†°Èó®Êï∞ÊçÆ
        const gatesResponse = await fetch('/data/gates.geojson');
        const gatesData = await gatesResponse.json();

        // Âä†ËΩΩÈ£üÂ†ÇÊï∞ÊçÆ
        const canteensResponse = await fetch('/data/canteens.geojson');
        const canteensData = await canteensResponse.json();

        // Âä†ËΩΩÂÆøËàçÊï∞ÊçÆ
        const dormsResponse = await fetch('/data/dorms.geojson');
        const dormsData = await dormsResponse.json();

        // ÊèêÂèñÂú∞ÁÇπ‰ø°ÊÅØ
        const gates = gatesData.features.map((feature, index) => ({
          id: `gate_${index + 1}`,
          name: `Ê†°Èó®${index + 1}`,
          coords: feature.geometry.coordinates,
          type: 'gate'
        }));

        const canteens = canteensData.features.map((feature, index) => ({
          id: `canteen_${index + 1}`,
          name: feature.properties.name || feature.properties['name:zh'] || `È£üÂ†Ç${index + 1}`,
          coords: feature.geometry.coordinates[0][0], // ÂèñÂ§öËæπÂΩ¢ÁöÑÁ¨¨‰∏Ä‰∏™ÁÇπ
          type: 'canteen'
        }));

        const dorms = dormsData.features.map((feature, index) => ({
          id: `dorm_${index + 1}`,
          name: feature.properties.name || feature.properties['name:zh'] || `ÂÆøËàç${index + 1}`,
          coords: feature.geometry.coordinates[0][0], // ÂèñÂ§öËæπÂΩ¢ÁöÑÁ¨¨‰∏Ä‰∏™ÁÇπ
          type: 'dorm'
        }));

        setLocations({ gates, canteens, dorms });
        console.log('Âú∞ÁÇπÊï∞ÊçÆÂä†ËΩΩÂÆåÊàê:', { gates, canteens, dorms });
        console.log('Ê†°Èó®Êï∞Èáè:', gates.length);
        console.log('È£üÂ†ÇÊï∞Èáè:', canteens.length);
        console.log('ÂÆøËàçÊï∞Èáè:', dorms.length);

        // ÊòæÁ§∫ÂâçÂá†‰∏™Âú∞ÁÇπÁöÑËØ¶ÁªÜ‰ø°ÊÅØ
        console.log('Ââç3‰∏™Ê†°Èó®:', gates.slice(0, 3));
        console.log('Ââç3‰∏™È£üÂ†Ç:', canteens.slice(0, 3));
        console.log('Ââç3‰∏™ÂÆøËàç:', dorms.slice(0, 3));

        // Ê£ÄÊü•Ëà™Á∫øÊï∞ÊçÆ
        console.log('Ëà™Á∫øÊï∞ÊçÆÁä∂ÊÄÅ:', routes ? 'Â∑≤Âä†ËΩΩ' : 'Êú™Âä†ËΩΩ');
        if (routes) {
          console.log('È£üÂ†ÇÂà∞ÂÆøËàçËà™Á∫øÊï∞Èáè:', routes.routes?.canteen_to_dorm?.length || 0);
          console.log('Ê†°Èó®Âà∞ÂÆøËàçËà™Á∫øÊï∞Èáè:', routes.routes?.gate_to_dorm?.length || 0);
          if (routes.routes?.canteen_to_dorm?.length > 0) {
            console.log('Á¨¨‰∏ÄÊù°È£üÂ†ÇÂà∞ÂÆøËàçËà™Á∫ø:', routes.routes.canteen_to_dorm[0]);
          }
        }

        setIsLoading(false);
      } catch (error) {
        console.error('Âä†ËΩΩÂú∞ÁÇπÊï∞ÊçÆÂ§±Ë¥•:', error);
        // ‰ΩøÁî®ÈªòËÆ§Êï∞ÊçÆ
        setLocations({
          gates: [
            { id: 'gate_1', name: '‰∏úÈó®', coords: [116.325263, 40.0053343], type: 'gate' },
            { id: 'gate_2', name: 'Ë•øÈó®', coords: [116.315263, 40.0053343], type: 'gate' },
            { id: 'gate_3', name: 'ÂçóÈó®', coords: [116.320263, 40.0003343], type: 'gate' }
          ],
          canteens: [
            { id: 'canteen_1', name: 'ËßÇÁï¥Âõ≠È§êÂéÖÔºà‰∏á‰∫∫Â§ßÈ£üÂ†ÇÔºâ', coords: [116.315263, 40.0053343], type: 'canteen' },
            { id: 'canteen_2', name: 'Á¥´ËçÜÂõ≠È§êÂéÖ', coords: [116.322763, 40.0103343], type: 'canteen' },
            { id: 'canteen_3', name: 'Ê∏ÖËä¨Âõ≠È§êÂéÖ', coords: [116.318763, 40.0073343], type: 'canteen' }
          ],
          dorms: [
            { id: 'dorm_1', name: 'Á¥´ËçÜÂ≠¶ÁîüÂÖ¨ÂØì1Âè∑Ê•º', coords: [116.320263, 40.0103343], type: 'dorm' },
            { id: 'dorm_2', name: 'Á¥´ËçÜÂ≠¶ÁîüÂÖ¨ÂØì2Âè∑Ê•º', coords: [116.321263, 40.0103343], type: 'dorm' },
            { id: 'dorm_3', name: 'Á¥´ËçÜÂ≠¶ÁîüÂÖ¨ÂØì3Âè∑Ê•º', coords: [116.322263, 40.0103343], type: 'dorm' }
          ]
        });

        setIsLoading(false);
      }
    };

    loadGeoJSONData();
  }, []);

  // Âä†ËΩΩËà™Á∫øÊï∞ÊçÆ
  useEffect(() => {
    console.log('ÂºÄÂßãÂä†ËΩΩËà™Á∫øÊï∞ÊçÆ...');

    // ÂàõÂª∫AbortControllerÁî®‰∫éË∂ÖÊó∂ÊéßÂà∂
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 30000); // 30ÁßíË∂ÖÊó∂

    fetch('/route_planning_results.json', {
      signal: controller.signal
    })
      .then(response => {
        console.log('Ëà™Á∫øÊï∞ÊçÆÂìçÂ∫îÁä∂ÊÄÅ:', response.status, response.statusText);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        clearTimeout(timeoutId);
        setRoutes(data);
        console.log('Ëà™Á∫øÊï∞ÊçÆÂä†ËΩΩÂÆåÊàê:', data);
        console.log('È£üÂ†ÇÂà∞ÂÆøËàçËà™Á∫øÊï∞Èáè:', data.routes?.canteen_to_dorm?.length || 0);
        console.log('Ê†°Èó®Âà∞ÂÆøËàçËà™Á∫øÊï∞Èáè:', data.routes?.gate_to_dorm?.length || 0);
        if (data.routes?.canteen_to_dorm?.length > 0) {
          console.log('Á¨¨‰∏ÄÊù°È£üÂ†ÇÂà∞ÂÆøËàçËà™Á∫ø:', data.routes.canteen_to_dorm[0]);
          console.log('Ëà™Á∫øËµ∑ÁÇπÂùêÊ†á:', data.routes.canteen_to_dorm[0].path[0]);
          console.log('Ëà™Á∫øÁªàÁÇπÂùêÊ†á:', data.routes.canteen_to_dorm[0].path[data.routes.canteen_to_dorm[0].path.length - 1]);
        }
      })
      .catch(error => {
        clearTimeout(timeoutId);
        if (error.name === 'AbortError') {
          console.error('Ëà™Á∫øÊï∞ÊçÆÂä†ËΩΩË∂ÖÊó∂');
        } else {
          console.error('Âä†ËΩΩËà™Á∫øÊï∞ÊçÆÂ§±Ë¥•:', error);
        }
        // ‰ΩøÁî®Á§∫‰æãÊï∞ÊçÆ
        setRoutes({
          routes: {
            canteen_to_dorm: [],
            gate_to_dorm: []
          }
        });
      });
  }, []);

  // Ê∑ªÂä†Êñ∞ËÆ¢Âçï
  const addOrder = (orderData) => {
    const newOrder = {
      id: `ORDER_${Date.now()}`,
      orderNumber: `THU_${Math.random().toString(36).substr(2, 9).toUpperCase()}`,
      createTime: new Date().toISOString(),
      status: 'pending', // pending, assigned, delivering, completed
      ...orderData
    };

    setOrders(prev => [...prev, newOrder]);

    // Â¶ÇÊûúÊ®°ÊãüÊ≠£Âú®ËøêË°åÔºåËá™Âä®ÂàÜÈÖçÊó†‰∫∫Êú∫
    if (isSimulationRunning) {
      assignDroneToOrder(newOrder);
    }
  };

  // ÂàÜÈÖçÊó†‰∫∫Êú∫Âà∞ËÆ¢Âçï
  const assignDroneToOrder = (order) => {
    console.log('ÂàÜÈÖçÊó†‰∫∫Êú∫Âà∞ËÆ¢Âçï:', order.id, 'Ëµ∑ÁÇπ:', order.startPoint, 'ÁªàÁÇπ:', order.endPoint);

    // Â∞ùËØïÊâæÂà∞ÂåπÈÖçÁöÑÂÆûÈôÖËà™Á∫ø
    let flightPath = null;
    let altitude = 100; // ÈªòËÆ§È´òÂ∫¶

    if (routes) {
      const bestRoute = findBestRoute(order.startPoint, order.endPoint);
      if (bestRoute) {
        flightPath = bestRoute.path;
        altitude = bestRoute.height || 100;
        console.log('‰ΩøÁî®ÂÆûÈôÖËà™Á∫ø:', bestRoute.from, '->', bestRoute.to, 'È´òÂ∫¶:', altitude);
      }
    }

    // Â¶ÇÊûúÊ≤°ÊúâÊâæÂà∞ÂåπÈÖçÁöÑËà™Á∫øÔºåÁîüÊàêÁõ¥Á∫øË∑ØÂæÑ
    if (!flightPath) {
      flightPath = generateFlightPath(order.startPoint, order.endPoint);
      console.log('‰ΩøÁî®ÁîüÊàêÁöÑÁõ¥Á∫øË∑ØÂæÑ');
    }

    console.log('ÊúÄÁªàÈ£ûË°åË∑ØÂæÑ:', flightPath);

    const availableDrone = {
      id: `DRONE_${Date.now()}`,
      orderId: order.id,
      status: 'assigned',
      startPosition: order.startPoint,
      targetPosition: order.endPoint,
      path: flightPath,
      currentPosition: order.startPoint,
      pathIndex: 0,
      speed: 10, // Á±≥/Áßí
      altitude: altitude
    };

    console.log('ÂàõÂª∫ÁöÑÊó†‰∫∫Êú∫ÂØπË±°:', availableDrone);

    setDrones(prev => [...prev, availableDrone]);

    // Êõ¥Êñ∞ËÆ¢ÂçïÁä∂ÊÄÅ
    setOrders(prev => 
      prev.map(o => 
        o.id === order.id ? { ...o, status: 'assigned' } : o
      )
    );
  };

  // Êü•ÊâæÊúÄ‰Ω≥Ëà™Á∫ø
  const findBestRoute = (start, end) => {
    if (!routes) {
      console.log('Ëà™Á∫øÊï∞ÊçÆÊú™Âä†ËΩΩ');
      return null;
    }

    console.log('Êü•ÊâæËà™Á∫øÔºåËµ∑ÁÇπÂùêÊ†á:', start, 'ÁªàÁÇπÂùêÊ†á:', end);

    // Ê†πÊçÆËµ∑ÁÇπÂíåÁªàÁÇπÊü•ÊâæÂåπÈÖçÁöÑËà™Á∫ø
    const allRoutes = [...routes.routes.canteen_to_dorm, ...routes.routes.gate_to_dorm];
    console.log('ÊÄªËà™Á∫øÊï∞Èáè:', allRoutes.length);

    // ÊâæÂà∞ÂåπÈÖçÁöÑËà™Á∫øÔºàÈÄöËøáÊØîËæÉÂùêÊ†áÔºâ
    const matchedRoute = allRoutes.find((route, index) => {
      if (!route.path || route.path.length === 0) {
        console.log(`Ëà™Á∫ø${index}Ê≤°ÊúâË∑ØÂæÑÊï∞ÊçÆ`);
        return false;
      }

      const routeStart = route.path[0];
      const routeEnd = route.path[route.path.length - 1];

      console.log(`Ê£ÄÊü•Ëà™Á∫ø${index}:`, route.from, '->', route.to);
      console.log('Ëà™Á∫øËµ∑ÁÇπ:', routeStart, 'Ëà™Á∫øÁªàÁÇπ:', routeEnd);

      // Ê£ÄÊü•Ëµ∑ÁÇπÊòØÂê¶ÂåπÈÖç
      const startMatches = isCoordinateNear(start, routeStart);
      // Ê£ÄÊü•ÁªàÁÇπÊòØÂê¶ÂåπÈÖç
      const endMatches = isCoordinateNear(end, routeEnd);

      console.log('Ëµ∑ÁÇπÂåπÈÖç:', startMatches, 'ÁªàÁÇπÂåπÈÖç:', endMatches);

      return startMatches && endMatches;
    });

    if (matchedRoute) {
      console.log('ÊâæÂà∞ÂåπÈÖçËà™Á∫ø:', matchedRoute.from, '->', matchedRoute.to);
      return matchedRoute;
    }

    // Â¶ÇÊûúÊ≤°ÊúâÊâæÂà∞ÂåπÈÖçÁöÑËà™Á∫øÔºåËøîÂõûnull
    console.log('Êú™ÊâæÂà∞ÂåπÈÖçËà™Á∫øÔºåËµ∑ÁÇπ:', start, 'ÁªàÁÇπ:', end);
    return null;
  };

  // Ê£ÄÊü•‰∏§‰∏™ÂùêÊ†áÊòØÂê¶Êé•ËøëÔºàÂÖÅËÆ∏‰∏ÄÂÆöÁöÑËØØÂ∑ÆÔºâ
  const isCoordinateNear = (coord1, coord2) => {
    if (!coord1 || !coord2) return false;

    const latDiff = Math.abs(coord1[1] - coord2[1]);
    const lngDiff = Math.abs(coord1[0] - coord2[0]);

    // ÂÖÅËÆ∏0.001Â∫¶ÁöÑËØØÂ∑ÆÔºàÂ§ßÁ∫¶100Á±≥Ôºâ
    return latDiff < 0.001 && lngDiff < 0.001;
  };

  // ÁîüÊàêÊó†‰∫∫Êú∫È£ûË°åË∑ØÂæÑ
  const generateFlightPath = (start, end) => {
    // ÁîüÊàê‰ªéËµ∑ÁÇπÂà∞ÁªàÁÇπÁöÑË∑ØÂæÑÁÇπ
    const path = [];
    const steps = 20; // Ë∑ØÂæÑÁÇπÊï∞Èáè

    for (let i = 0; i <= steps; i++) {
      const progress = i / steps;
      const lat = start[1] + (end[1] - start[1]) * progress;
      const lng = start[0] + (end[0] - start[0]) * progress;
      path.push([lng, lat]);
    }

    return path;
  };

  // ÂºÄÂßãÊ®°Êãü
  const startSimulation = () => {
    console.log('ÂºÄÂßãÊ®°ÊãüÔºåÂΩìÂâçËÆ¢ÂçïÊï∞Èáè:', orders.length);
    setIsSimulationRunning(true);

    // ‰∏∫ÊâÄÊúâÂæÖÂàÜÈÖçÁöÑËÆ¢ÂçïÂàÜÈÖçÊó†‰∫∫Êú∫
    orders.forEach(order => {
      if (order.status === 'pending') {
        console.log('‰∏∫ËÆ¢ÂçïÂàÜÈÖçÊó†‰∫∫Êú∫:', order.id);
        assignDroneToOrder(order);
      }
    });
  };

  // ÂÅúÊ≠¢Ê®°Êãü
  const stopSimulation = () => {
    setIsSimulationRunning(false);
  };

  // ÈáçÁΩÆÁ≥ªÁªü
  const resetSystem = () => {
    setOrders([]);
    setDrones([]);
    setSelectedOrder(null);
    setIsSimulationRunning(false);
  };

  // Êõ¥Êñ∞Êó†‰∫∫Êú∫‰ΩçÁΩÆÔºàÊ®°ÊãüÈ£ûË°åÔºâ
  useEffect(() => {
    if (!isSimulationRunning) return;

    const interval = setInterval(() => {
      console.log('Êõ¥Êñ∞Êó†‰∫∫Êú∫‰ΩçÁΩÆÔºåÂΩìÂâçÊó†‰∫∫Êú∫Êï∞Èáè:', drones.length);

      setDrones(prev => 
        prev.map(drone => {
          console.log('Â§ÑÁêÜÊó†‰∫∫Êú∫:', drone.id, 'Áä∂ÊÄÅ:', drone.status, 'Ë∑ØÂæÑÈïøÂ∫¶:', drone.path?.length);

          if (drone.status === 'assigned') {
            // ÂºÄÂßãÈÖçÈÄÅ
            console.log('Êó†‰∫∫Êú∫ÂºÄÂßãÈÖçÈÄÅ:', drone.id);
            return { ...drone, status: 'delivering' };
          } else if (drone.status === 'delivering' && drone.path && drone.path.length > 0) {
            // Êõ¥Êñ∞Êó†‰∫∫Êú∫‰ΩçÁΩÆ - Ê≤øÁùÄË∑ØÂæÑÈÄêÊ≠•ÁßªÂä®
            const nextIndex = Math.min((drone.pathIndex || 0) + 1, drone.path.length - 1);
            const newStatus = nextIndex >= drone.path.length - 1 ? 'completed' : 'delivering';

            console.log('Êó†‰∫∫Êú∫‰ΩçÁΩÆÊõ¥Êñ∞:', drone.id, '‰ªéÁ¥¢Âºï', drone.pathIndex, 'Âà∞', nextIndex, 'Êñ∞Áä∂ÊÄÅ:', newStatus);

            // Â¶ÇÊûúÂÆåÊàêÔºåÊõ¥Êñ∞ËÆ¢ÂçïÁä∂ÊÄÅ
            if (newStatus === 'completed') {
              setOrders(prev => 
                prev.map(o => 
                  o.id === drone.orderId ? { ...o, status: 'completed' } : o
                )
              );
            }

            return {
              ...drone,
              currentPosition: drone.path[nextIndex],
              pathIndex: nextIndex,
              status: newStatus
            };
          }
          return drone;
        })
      );
    }, 1000); // ÊØè1ÁßíÊõ¥Êñ∞‰∏ÄÊ¨°

    return () => clearInterval(interval);
  }, [isSimulationRunning]);

